INSERT INTO review(course_name, given_rating, title, review, reviewer, post_date, teacher_id) VALUES('Pilka Nozna', 4.5, '', 'Za obecnosc 5.0 (mozliwosc odrobek nieobecnosci) Mialem z tym panem 1 kurs i teraz zapisalem sie jeszcze raz. Bardzo milo go wspominam. Luzne zajecia, choc na pierwszych dosyc duzo mielismy cwiczen z pilkami i bez zanim zaczelismy grac i troche to bylo meczace... ale juz od polowy semestru rozgrzewka trwala chwile i po niej gralismy w pile. POLECAM', '', '2006-03-08 17:22', 5);
